import React, { useState } from 'react';
import './Navbar.css';

function Navbar(props) {
    const [searchTerm, setSearchTerm] = useState("");
    const [searchData, setSearchData] = useState([]);
    const [dataReady, setDataReady] = useState(false);

    function handleChange(evt) {
        setSearchTerm(evt.target.value);
    }

    async function hitAPIForSearch(query = "") {
        console.log("Hello");
        const response = await axios.get(`${this.props.apiURL}?s=${query}&apikey=${this.props.apiKey}`);
        setSearchData(response.data.Search);
        setDataReady(true);
    }

    function handleSubmit(evt) {
        evt.preventDefault();
        hitAPIForSearch(searchTerm);
        if(dataReady) {
            props.searchFunction(searchData);
        }
    }

    return (<div>
        <h4>Moview</h4>
        <form onSubmit={handleSubmit}>
            <input name="search" onChange={handleChange} value={searchTerm} placeholder="Search for a movie" />
            <button>Submit</button>
        </form>
    </div>);
}

export default Navbar;
