class ListHolder extends React.Component {
    constructor(props) {
        super(props);
        this.state = { searchData: [], dataReady: false};
    }

    async hitAPIForSearch() {
        console.log("Hello");
        const response = await axios.get(this.props.apiURL);
        this.setState({ searchData: response.data.Search, dataReady: true});
    }

    componentDidMount() {
        this.hitAPIForSearch();
    }

    renderListItems() {
        if(!this.state.dataReady) {
            return (<h1>Loading...</h1>);
        } else if(this.state.searchData === undefined) {
            return;
        } else {

            // return <h1>{searchData[0].Title}</h1>
