import axios from 'axios';
import React, { useEffect, useState } from 'react';
import Tile from './Tile';

function ListHolder(props) {
    const [searchData, setSearchData] = useState([]);
    const [dataReady, setDataReady] = useState(false);

    async function hitAPIForSearch() {
        const response = await axios.get(props.apiURL);
        setSearchData(response.data.Search);
        setDataReady(true);
    }

    useEffect(() => hitAPIForSearch());

    function renderListItems() {
        if(!dataReady) {
            return (<h1>Loading...</h1>);
        } else if(searchData === undefined) {
            return;
        } else {
            return <h1>{searchData.Tile}</h1>
            /* searchData.map(item => {
                return <Tile key={item.imdbID} imgSrc={item.Poster} name={item.Title} id={item.imdbID} />
            }); */
        }
    }

    return (<div>
        <h1>List of Items</h1>
        {renderListItems()}
    </div>);
}

export default ListHolder;
