import './App.css';
import Navbar from './Components/Navbar/Navbar';
import ListHolder from './Components/ListPage/ListHolder';
import DetailTile from './Components/DetailPage/DetailTile';
import { Switch, Route } from 'react-router-dom';
import { useState } from 'react';

const API_CONSTS = ["1ab924a0", "http://www.omdbapi.com/"];

function App() {
  const [searchTerm, setSearchTerm] = useState("");
  let searchFunction;

  function getSearchTerm(value) {
    setSearchTerm(value);
  }

  function getSearchFunction(fn) {
    searchFunction = fn;
    console.log(searchFunction + " Hiuoi");
  }

  let apiURL = `${API_CONSTS[1]}?s=${searchTerm}&apikey=${API_CONSTS[0]}`

  return (
    <div className="App">
      <Navbar getSearchTermFn={getSearchTerm} searchFunction={searchFunction()} />
      <Switch>
        <Route exact path="/">
          <ListHolder apiURL={`${API_CONSTS[1]}?s=`} searchTerm={`${searchTerm}&apikey=`} apiKey={API_CONSTS[0]} searchFunction={getSearchFunction} />
        </Route>
        <Route exact path="/:movieID">
          <DetailTile api_URL={apiURL} />
        </Route>
        <Route render={() => <h1 id="error-message">Error 404: Page not found</h1>} />
      </Switch>
    </div>
  );
}

export default App;
